import random
import sys


def get_user_input():
    """Get and validate user input for a guess."""
    while True:
        try:
            user_input = input("Enter your guess (1-100) or 'quit' to exit: ").strip().lower()

            if user_input == 'quit':
                return None

            guess = int(user_input)

            if guess < 1 or guess > 100:
                print("Please enter a number between 1 and 100.")
                continue

            return guess

        except ValueError:
            print("Invalid input. Please enter a valid number between 1 and 100.")


def play_game():
    """Play a single game session."""
    # Generate random number between 1 and 100
    target_number = random.randint(1, 100)
    attempts = 0
    guessed = False

    print("Welcome to the Number Guessing Game!")
    print("I'm thinking of a number between 1 and 100.")
    print("Enter 'quit' at any time to exit the game.")

    while not guessed:
        guess = get_user_input()

        # Handle quit command
        if guess is None:
            print("Thanks for playing! Goodbye!")
            return False

        attempts += 1

        if guess < target_number:
            print("Too low!")
        elif guess > target_number:
            print("Too high!")
        else:
            print(f"Congratulations! You guessed the number in {attempts} attempts.")
            guessed = True

    return True


def main():
    """Main game loop that allows multiple games."""
    while True:
        # Play one game
        game_completed = play_game()

        # If user quit during game, exit completely
        if not game_completed:
            sys.exit(0)

        # Ask if user wants to play again
        while True:
            try:
                play_again = input("\nDo you want to play again? (yes/no): ").strip().lower()

                if play_again in ['yes', 'y']:
                    print("\nStarting a new game...\n")
                    break
                elif play_again in ['no', 'n']:
                    print("Thanks for playing! Goodbye!")
                    sys.exit(0)
                else:
                    print("Please enter 'yes' or 'no'.")

            except KeyboardInterrupt:
                print("\n\nThanks for playing! Goodbye!")
                sys.exit(0)


if __name__ == "__main__":
    main()

